import React, { useState, useEffect } from "react";
import { Route, Routes } from "react-router-dom";

import JoblyApi from "./api";

import Home from "./Home.js";
import CompanyDetails from "./CompanyDetails.js";
import Jobs from "./Jobs.js";
import Companies from "./Companies.js";
import EditUserForm from "./EditUserForm.js";
import LoginForm from "./LoginForm.js";
import NewUserForm from "./NewUserForm.js";

function MyRoutes() {

    const [jobs, setJobs] = useState([]);
    const [companies, setCompanies] = useState([]);



    //adds a new user to the db with a post request
    const registerUser = (data) => {
        JoblyApi.registerUser(data)
    };
    //Logs in user
    const userLogin = (data) => {
        JoblyApi.userLogin(data)
    };

    //Searches for Job
    const jobSearch = (data) => {
        JoblyApi.getJob(data)

    }

    //Searches for company
    const companySearch = (data) => {
        JoblyApi.getCompany(data)

    }

    //Updates User Profile Info
    const profileUpdate = (username, data) => {
        JoblyApi.userLogin(data)

    }

    //Applies user to job
    const addJob = (data) => {
        JoblyApi.userLogin(data)

    }

    //Set the company and job list. 
    // useEffect(() => {
    //     async function getJobs() {
    //         let companies = await JoblyApi.getAllCompanies();
    //         setCompanies(companies);
    //     };
    //     async function getCompanies() {
    //         let jobs = await JoblyApi.getAllJobs();
    //         setJobs(jobs);
    //     };

    //     getJobs();
    //     getCompanies();

    // }, []);


    return (
        <div>
            <Routes>
                <Route exact path="/" element={<Home />} />

                <Route exact path="/companies" element={<Companies companies={companies} companySearch={companySearch} />} />

                <Route exact path="/companies/:handle" element={<CompanyDetails companySearch={companySearch} />} />

                <Route exact path="/jobs" element={<Jobs jobs={jobs} jobSearch={jobSearch} />} />

                <Route exact path="/login" element={<LoginForm userLogin={userLogin} />} />

                <Route exact path="/signup" element={<NewUserForm registerUser={registerUser} />} />

                <Route exact path="/profile" element={<EditUserForm profileUpdate={profileUpdate} />} />

                {/* 
                <Route path="*" element={<Navigate replace to="/colors" />} /> */}
            </Routes>


        </div>
    );
}

export default MyRoutes
