import './App.css';
import React, { useState, useEffect } from "react";
import { BrowserRouter } from "react-router-dom";

import MyRoutes from './routes-nav/MyRoutes.js'
import Nav from './routes-nav/MyNav.js'
import UserContext from "./profile/UserContext";
import useLocalStorage from "./hooks/useLocalStorage";


function App() {

  const [currentUser, setCurrentUser] = useState(null);

  const [applications, setApplications] = useState(null);

  const [token, setToken] = useLocalStorage(null);

  const [userInfoLoaded, setUserInfoLoaded] = useState(false)


  //Register new User
  async function registerUser(data) {
    const res = await JoblyApi.registerUser(data);
    setToken(res);
  }

  //Login User
  async function loginUser(data) {
    const res = await JoblyApi.loginUser(data);
    setToken(res);
  }

  //Update User
  async function updateUser(data) {
    const user = await JoblyApi.updateUser(currentUser.username, data)
    setCurrentUser(user)
  }



  // Check for token in local storage. 
  useEffect(() => {
    async function checkToken() {
      if (token) {
        let { username } = jwt_decode(token);
        JoblyApi.token = token;
        const user = await JoblyApi.getUser(username);
        setCurrentUser(user);
        setApplications(user.applications)
        setUserInfoLoaded(true)


      }
    }
    checkToken();
  }, [token]);



  return (
    <div>
      <BrowserRouter>
        <UserContext.Provider value={{ currentUser, setCurrentUser, applications, setApplications, setToken, token, userInfoLoaded, setUserInfoLoaded, loginUser, registerUser, updateUser }}>

          <Nav />
          <main>
            <MyRoutes />
          </main>
        </UserContext.Provider >
      </BrowserRouter>



    </div>
  );
}

export default App;
