import React, { useState, useContext, useEffect } from "react";
import { Navigate } from "react-router-dom";
import UserContext from "../profile/UserContext";
import SearchForm from "../common/SearchForm";
import JoblyApi from "../api/api";
import JobCard from "./JobCard";

const Jobs = () => {


    const [jobs, setJobs] = useState([]);



    //Searches for Job
    async function getJobs(data) {
        let jobs = await JoblyApi.getAllJobs(data.search);
        setJobs(jobs);
    };


    // Set the job list. 
    useEffect(() => {
        async function getJobs() {
            let jobs = await JoblyApi.getAllJobs();
            setJobs(jobs);
        };

        getJobs();

    }, []);



    const { currentUser } = useContext(UserContext);

    if (!currentUser) {
        return <Navigate replace to="/login" />
    }

    return (
        <div>
            jobs
            <SearchForm searchFunction={getJobs} />
            {jobs.map(job => (
                <JobCard hasAppliedToJob={hasAppliedToJob} key={job.id} id={job.id} title={job.title} salary={job.salary} equity={job.equity} companyName={job.companyName} />
            ))}
        </div>
    )


};
export default Jobs;
