import React, { useState, useContext } from "react";
import { useNavigate, Navigate } from "react-router-dom";
import UserContext from "../profile/UserContext";

const EditUserForm = ({ updateUser }) => {

    const { currentUser, userInfoLoaded } = useContext(UserContext);
    const [formData, setFormData] = useState()
    let INITIAL_STATE = { username: "", password: "", firstName: "", lastName: "", email: "" };
    setFormData(INITIAL_STATE);

    if (userInfoLoaded) {
        INITIAL_STATE = { username: currentUser.username, password: currentUser.password, firstName: currentUser.firstName, lastName: currentUser.lastName, email: currentUser.email };
        setFormData(INITIAL_STATE)
    }

    const navigate = useNavigate();





    if (!currentUser && userInfoLoaded) {
        return <Navigate replace to="/login" />
    }

    /** Send {type, name, serve, recipe, description} to parent
     *    & clear form. */


    const handleSubmit = evt => {
        evt.preventDefault();

        let profileData = {
            firstName: formData.firstName,
            lastName: formData.lastName,
            email: formData.email,
            password: formData.password,
        };

        updateUser(profileData)
        setFormData(INITIAL_STATE);
        navigate(`/`);
    };

    /** Update local state w/curr state of input elem */

    const handleChange = evt => {
        const { name, value } = evt.target;
        setFormData(fData => ({
            ...fData,
            [name]: value
        }));
    };

    /** render form */

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="username">Username</label>

            <p className="form-control-plaintext">{currentUser.username}</p>

            <label htmlFor="password">Password</label>
            <br />
            <input
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="firstName">First Name</label>
            <br />
            <input
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="lastName">Last Name</label>
            <br />
            <input
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="email">Email</label>
            <br />
            <input
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
            />
            <br />

            <button>Submit</button>
        </form>
    )

};
export default EditUserForm;
