import React, { useState, useEffect, useContext } from "react";
import { Route, Routes, Navigate } from "react-router-dom";

import JoblyApi from "../api/api";

import Home from "../home/Home.js";
import CompanyDetails from "../companies/CompanyDetails.js";
import Jobs from "../jobs/Jobs.js";
import Companies from "../companies/Companies.js";
import EditUserForm from "../profile/EditUserForm.js";
import LoginForm from "../profile/LoginForm.js";
import NewUserForm from "../profile/NewUserForm.js";
import UserContext from "../profile/UserContext";
import jwt_decode from "jwt-decode";


function MyRoutes() {

    const { currentUser, setCurrentUser, setApplicationIds, token, setToken } = useContext(UserContext);


    //set user info
    function setUserInfo(user, token) {
        setCurrentUser(user);
        // setApplicationIds(new Set(user.applications));
        // setToken(token);
    }



    //Register new User
    async function registerUser(data) {
        const res = await JoblyApi.registerUser(data);

        setUserInfo(res.user, res.token)
    }

    //Login User
    async function loginUser(data) {
        const res = await JoblyApi.loginUser(data);
        setToken(res);
    }

    //Update User
    async function updateUser(data) {
        const user = await JoblyApi.updateUser(currentUser.username, data)
        setCurrentUser(user)
    }



    // Check for token in local storage. 
    useEffect(() => {
        async function checkToken() {
            if (token) {
                let { username } = jwt_decode(token);
                JoblyApi.token = token
                const user = await JoblyApi.getUser(username)
                setCurrentUser(user)
            }
        }
        checkToken();
    }, [token]);




    return (
        <div>
            <Routes>
                <Route exact path="/" element={<Home />} />

                <Route exact path="/companies" element={<Companies />} />

                <Route exact path="/companies/:handle" element={<CompanyDetails />} />

                <Route exact path="/jobs" element={<Jobs />} />

                <Route exact path="/login" element={<LoginForm loginUser={loginUser} />} />

                <Route exact path="/signup" element={<NewUserForm registerUser={registerUser} />} />

                <Route exact path="/profile" element={<EditUserForm updateUser={updateUser} />} />


                <Route path="*" element={<Navigate replace to="/" />} />
            </Routes>


        </div>
    );
}

export default MyRoutes
