import React, { useState, useContext } from "react";
import { useNavigate, Navigate } from "react-router-dom";
import UserContext from "../profile/UserContext";

const EditUserForm = ({ updateUser }) => {

    const { currentUser } = useContext(UserContext);

    const INITIAL_STATE = { username: currentUser.username, password: currentUser.password, firstName: currentUser.firstName, lastName: currentUser.lastName, email: currentUser.email };
    const [formData, setFormData] = useState(INITIAL_STATE);

    const navigate = useNavigate();





    if (!currentUser) {
        return <Navigate replace to="/login" />
    }

    /** Send {type, name, serve, recipe, description} to parent
     *    & clear form. */

    const handleSubmit = evt => {
        evt.preventDefault();
        updateUser(formData)
        setFormData(INITIAL_STATE);
        navigate(`/`);
    };

    /** Update local state w/curr state of input elem */

    const handleChange = evt => {
        const { name, value } = evt.target;
        setFormData(fData => ({
            ...fData,
            [name]: value
        }));
    };

    /** render form */

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="username">Username</label>
            <br />
            <input
                id="username"
                name="username"
                value={formData.username}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="password">Password</label>
            <br />
            <input
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="firstName">First Name</label>
            <br />
            <input
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="lastName">Last Name</label>
            <br />
            <input
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="email">Email</label>
            <br />
            <input
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
            />
            <br />

            <button>Submit</button>
        </form>
    )

};
export default EditUserForm;
