import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
    Card,
    CardBody,
    CardTitle,
    CardText,
    ListGroup,
    ListGroupItem
} from "reactstrap";


import CompanyCard from "./CompanyCard";

const Companies = ({ companies }) => {
    return (
        <div>
            companies
            {companies.map(company => (
                <p><CompanyCard name={company.name} description={company.description} logoUrl={company.logoUrl} /></p>
            ))}
        </div>
    )

};
export default Companies;
