import React, { useState } from "react";
import {
    Card, CardBody, CardTitle, CardText, ListGroup,
    ListGroupItem
} from "reactstrap";

//Company Card container: renders any individual item from list of companies
const CompanyCard = ({ name, description, logoUrl }) => {


    return (
        <section>
            <Card>
                <CardBody>
                    <Link to={`/${type}/${item.id}`} key={item.id}>
                        <ListGroupItem>{item.name}</ListGroupItem>
                    </Link>
                    <CardTitle className="font-weight-bold text-center">
                        {name}
                    </CardTitle>
                    <CardText className="font-italic">{description}</CardText>
                    {(logoUrl ? <img src={logoUrl}></img> : "no image")}
                </CardBody>
            </Card>
        </section>
    );
}
export default CompanyCard;
