import React, { useState, useContext, useEffect } from "react";
import { useNavigate, Navigate } from "react-router-dom";
import UserContext from "../profile/UserContext";

import jwt_decode from "jwt-decode";
import JoblyApi from "../api/api";

const EditUserForm = ({ updateUser }) => {

    const { currentUser, setCurrentUser, token, setApplications, setUserInfoLoaded } = useContext(UserContext);
    console.log(`Profile:${currentUser}`)
    let INITIAL_STATE;


    useEffect(() => {
        if (token && currentUser) {

            INITIAL_STATE = { password: "", firstName: currentUser.firstName, lastName: currentUser.lastName, email: currentUser.email };
        } else if (token && !currentUser) {
            INITIAL_STATE = { username: "", password: "", firstName: "", lastName: "", email: "" };
        }
        // console.log("uselayouteffect going")
        // async function getUser() {
        //     if (token && !currentUser) {
        //         console.log("getting userData")

        //         let { username } = jwt_decode(token);
        //         JoblyApi.token = token;
        //         const user = await JoblyApi.getUser(username);
        //         console.log(user)
        //         setCurrentUser(user)

        //     }

        // }
        // getUser()
    }, [])




    console.log("doing the problematic thing")


    INITIAL_STATE = { password: "", firstName: currentUser.firstName, lastName: currentUser.lastName, email: currentUser.email };
    const [formData, setFormData] = useState(INITIAL_STATE);

    const navigate = useNavigate();

    if (!currentUser) {
        return <Navigate replace to="/login" />
    }





    /** Send {type, name, serve, recipe, description} to parent
     *    & clear form. */


    const handleSubmit = evt => {
        evt.preventDefault();

        let profileData = {
            firstName: formData.firstName,
            lastName: formData.lastName,
            email: formData.email,
            password: formData.password,
        };

        updateUser(profileData)
        setFormData(INITIAL_STATE);
        navigate(`/`);
    };

    /** Update local state w/curr state of input elem */

    const handleChange = evt => {
        const { name, value } = evt.target;
        setFormData(fData => ({
            ...fData,
            [name]: value
        }));
    };

    /** render form */

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="username">Username</label>

            <p className="form-control-plaintext">{currentUser.username}</p>

            <label htmlFor="password">Password</label>
            <br />
            <input
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="firstName">First Name</label>
            <br />
            <input
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="lastName">Last Name</label>
            <br />
            <input
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
            />
            <br />
            <label htmlFor="email">Email</label>
            <br />
            <input
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
            />
            <br />

            <button>Submit</button>
        </form>
    )

};
export default EditUserForm;
