        <div>
            <h1>Jobly</h1>
            <h3>All the jobs in one, convenient place.</h3>
            {(currentUser ? <div>Welcome Back {currentUser.username || currentUser.firstName}</div> : homebuttons)}

        </div>
    )

};
export default Home;
