import React, { useState, useContext, useEffect } from "react"; import {
    Card, CardBody, CardTitle
} from "reactstrap";
import JoblyApi from "../api/api";
import { Navigate } from "react-router-dom";
import UserContext from "../profile/UserContext";




const JobCard = ({ id, title, salary, equity, companyName }) => {

    const { currentUser } = useContext(UserContext);
    const [hasApplied, setHasApplied] = useState()




    // useEffect(() => {
    //     async function determineApplied() {
    //         let applicationIds = currentUser.applications
    //         setHasApplied(applicationIds.includes(id))
    //     };

    //     determineApplied();

    // }, [hasApplied]);

    //Apply to Job
    async function jobApply() {
        let updateUser = await JoblyApi.jobApply(currentUser.username, id);
        setHasApplied(true);

    };





    return (
        <section>
            <Card>
                <CardBody>
                    <CardTitle className="font-weight-bold text-center">
                        {title}
                    </CardTitle>
                    {companyName}
                    <p>Salary: {salary}</p>
                    <p>Equity: {equity}</p>
                    <button
                        className="btn btn-danger font-weight-bold text-uppercase float-right"
                        onClick={jobApply}
                        disabled={hasApplied}
                    >
                        {hasApplied ? "Applied" : "Apply"}
                    </button>

                </CardBody>
            </Card>
        </section>
    )

};
export default JobCard;
